mapper.select:
SELECT id, name, user_id, template_id, tags, config
FROM device
WHERE id = #{id}

SELECT id, name, user_id, template_id, tags, config
FROM device

    AND id = #{id}
    AND name LIKE CONCAT('%', #{name}, '%')
    AND user_id = #{userId}
    AND template_id = #{templateId}

SELECT id, name, user_id, template_id, tags, config
FROM device
WHERE user_id = #{userId}

SELECT id, name, user_id, template_id, tags, config
FROM device
WHERE template_id = #{templateId}
