mapper.update:
UPDATE message
title = #{title}, content = #{content}, type = #{type, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, status = #{status, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}, read_time = #{readTime}, delete_time = #{deleteTime}
WHERE id = #{id}

UPDATE message
SET status = #{status, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler},

    read_time = #{updateTime}
    delete_time = #{updateTime}

WHERE id = #{id}

mapper.select:
SELECT * FROM message
WHERE receive_id = #{receiveId}

    AND type = #{type, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


    AND status = #{status, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}

ORDER BY create_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM message
WHERE receive_id = #{receiveId}

    AND type = #{type, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}


    AND status = #{status, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler}
